package corejava.loops;

public class Loop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Loop2 lp = new Loop2();
				int num = 5;
				lp.perform(num);

	}

	 void perform(int num) {
		// TODO Auto-generated method stub
		 int i = 1;
		 while(i<=num) {
			 System.out.print(i+" ");
			 i++;
		 }
		
	}

}
