package corejava.loops;

public class Loop3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Loop3 lp = new Loop3();
		lp.perform();

	}

	 void perform() {
		// TODO Auto-generated method stub
		 int num = 1;
		 while(num <= 9) {
			 System.out.print(num+" ");
			 num += 2;
		 }
		
	}

}
