package corejava.loops;

public class Loop4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Loop4 lp = new Loop4();
		lp.perform();

	}

	 void perform() {
		// TODO Auto-generated method stub
		 int num = 3;
		 while(num <= 15) {
			 System.out.print(num+" ");
			 num += 3;
		 }
		
	}

}
